const { ggb1, rte1 } = components;

const readOrder = [rte1, ggb1];

ggb1.instance.registerObjectUpdateListener("time", timeUpdate);

function timeUpdate() {
  const time = ggb1.instance.getValue("time");
  const coins = ggb1.instance.getValue("coinCount");
  if (time > 0) {
    rte1.updateData({
      text: `<p>Practice sorting $5$ coins to start.<br />
<br />
Time: $${time}$ seconds<br />
&nbsp;&nbsp;<br />
Coins sorted: $${coins}$</p>`,
    });
  }

  if (ggb1.instance.getValue("allCorrect")) {
    ggb1.instance.stopAnimation();
    ggb1.instance.setAnimating("time", false);

    const partialString = coins === 1 ? "coin was" : "coins were";
    const timeString = time === 1 ? "second" : "seconds";

    utils.screenReader.sendMessage(
      "The time has stopped. " +
        coins +
        " " +
        partialString +
        " correctly sorted in " +
        time +
        " " +
        timeString +
        "."
    );
  }

  if (
    time === ggb1.instance.getValue("maxTime") &&
    !ggb1.instance.getValue("allCorrect")
  ) {
    ggb1.instance.setValue("maxTime", ggb1.instance.getValue("maxTime") + 15);
    ggb1.instance.setAnimating("time", true);
    ggb1.instance.startAnimation();
  }
}

